INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('084d98a7-398b-456a-ac76-bf24752eee36', '45d02d9d-0cda-4f57-8166-eb7a34f9f964', 'CLOSED_SINGLE', 'Azərbaycan Respublikasının Konstitusiyasına əsasən aşağıda göstərilən müddəalardan neçəsi doğrudur? 1. Azərbaycan dövləti bazar münasibətləri əsasında müxtəlif mülkiyyət növlərinə əsaslanaraq xalqın rifahının yüksəldilməsinə xidmət etməsi nəzərdə tutulan sosial yönümlü iqtisadiyyatına inkişafına şərait yaradır. 2. Azərbaycan Respublikasında din dövlətin tərkib hissəsidir. 3. Valideynləri və ya qəyyumları olmayan, valideyn qayğısından məhrum olan uşaqlar dövlətin nəzarətindədirlər. 4. Dövlət sərhədlərinin müəyyənləşdirilməsi məsələsi yalnız referendumla həll edilə bilər. 5. Konstitusiyanın müddəalarına əsasən qanunvericilik və icra hakimiyyətləri qarşılıqlı fəaliyyət göstərirlər.', 1, 7, NULL, now(), now());
INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('094c82ea-678e-4394-ae44-d222c8887067', '45d02d9d-0cda-4f57-8166-eb7a34f9f964', 'CLOSED_SINGLE', 'Azərbaycan Respublikasının Prokurorluğu prokurorluqla bağlı qanun layihəsini qanunvericilik təşəbbüsü qaydasında Milli Məclisin müzakirəsinə təqdim edib. Layihədə prokurorluqların istintaq üzrə səlahiyyət bölgüsü müəyyən olunur. Müzakirə zamanı bəzi deputatlar layihədə dəyişiklik etmək istədiklərini bildiriblər, lakin Azərbaycan Respublikasının Baş prokurorluğu bu dəyişikliklərə razılıq vermir. Bundan başqa, Azərbaycan Respublikasının Baş prokurorluğu layihənin təcili qaydada baxılmasını tələb edir. Azərbaycan Respublikasının Konstitusiyasına əsasən situasiya ilə bağlı doğru mülahizəni müəyyən edin.', 1, 8, NULL, now(), now());
