INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('c32b7e53-6206-495f-999b-f49e07394c57', '45d02d9d-0cda-4f57-8166-eb7a34f9f964', 'CLOSED_SINGLE', 'Azərbaycan Respublikasının Konstitusiyasına əsasən bələdiyyələrin işinin təşkili ilə bağlı doğru olmayanı müəyyən edin. 1. Bələdiyyənin iclaslarını bələdiyyənin sədri çağırır. 2. Bələdiyyələr seçkilər əsasında yaradılır. 3. Bələdiyyələr öz fəaliyyətini iclaslar vasitəsi ilə həyata keçirir. 4. Bələdiyyələr öz fəaliyyətini daimi və başqa komissiyalar vasitəsi ilə həyata keçirir. 5. Bələdiyyənin iclaslarında baxılan məsələlərə dair qərarlar qəbul edilir.', 1, 148, NULL, now(), now());
INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('c524982c-c1e8-4999-84fb-d4e673f3a69e', '45d02d9d-0cda-4f57-8166-eb7a34f9f964', 'CLOSED_SINGLE', 'Azərbaycan Respublikasının Konstitusiyasına əsasən Milli Məclisin müəyyən etdiyi ümumi qaydalar və həll etdiyi məsələlərlə bağlı məntiqi ardıcıllığı tamamlayın. beynəlxalq müqavilələrin təsdiqi və ləğvi, dövlət qulluğu, inzibati ərazi bölgüsü', 1, 149, NULL, now(), now());
INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('c531c2e3-a091-4a4f-8ff2-279ab4c8133b', '45d02d9d-0cda-4f57-8166-eb7a34f9f964', 'CLOSED_SINGLE', '2023-cı ilin sonunda Azərbaycan Respublikası Prezidentinin təqdim etdiyi dövlət büdcəsi Milli Məclis tərəfindən təsdiq edilir. Büdcə layihəsi dövlətin gələcək illərdəki iqtisadi inkişafını təmin etmək məqsədilə hazırlanıb və çoxsaylı infrastruktur layihələrinin icrasını nəzərdə tutur. Milli Məclis büdcəninin təsdiq edilməsi prosesini başa çatdırdıqdan sonra, Nazirlər Kabineti bu büdcənin icrasını təmin etməyə başlayır. Büdcə icrası zamanı Milli Məclisin bəzi deputatları irad bildirərək, müəyyən sahələr üzrə vəsaitlərin yetərli olmadığını qeyd edirlər və büdcə icrasına birbaşa müdaxilə etmək təklifini irəli sürürlər. Onlar, xüsusilə sosial sahələrə və kənd təsərrüfatına yönəldilən vəsaitlərdə dəyişikliklər və yeni təşviqlərin tətbiq edilməsini tələb edirlər. Nazirlər Kabineti isə bu təkliflərə qarşılıq olaraq bildirir ki, təsdiq edilmiş dövlət büdcəsinin icrasını təmin etmək yalnız Nazirlər Kabinetinin səlahiyyətindədir. Milli Məclisin funksiyası büdcənin icrasına nəzarət etməkdir. Azərbaycan Respublikasının Konstitusiyasına əsasən situasiya ilə bağlı doğru mülahizənin göstərildiyi variantı seçin.', 1, 150, NULL, now(), now());
