INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('5381a759-f8d2-4b86-b0b2-0e68842c7f4a', '4614a359-6770-44d8-a6f8-2533d961b7aa', 'CLOSED_SINGLE', 'A, bir dövlət müəssisəsində mütəxəssis olaraq çalışır və 5 illik müqavilə ilə işləyir. Müəssisədə son zamanlar struktur dəyişiklikləri baş verir və yeni rəhbərlik işçilərin əmək şəraitini yenidən nəzərdən keçirir. Yeni təklif olunan əmək şəraiti A-nın əvvəlki iş şəraitindən əhəmiyyətli dərəcədə fərqlidir. A bu dəyişiklikləri qəbul etmir, çünki təklif edilən şərait əvvəlki iş şəraitinə nisbətən daha ağırdır və ona uyğun gəlmir. Lakin, müəssisə rəhbərliyi bildirmişdir ki, əgər A yeni şəraiti qəbul etməsə, müqaviləyə xitam veriləcəkdir. A bu təklifə razılaşmır və əmək müqaviləsinin ləğvini tələb edir. Eyni zamanda, müəssisənin mülkiyyətçisi də dəyişib, amma bu dəyişikliyin işçilər üçün hər hansı bir hüquqi nəticəsi olmadığı bildirilir. Yuxarıdakı vəziyyətə əsaslanaraq, A-nın əmək müqaviləsinə xitam verilməsi üçün hansı əsaslardan biri düzgün tətbiq oluna bilər?', 1, 16, NULL, now(), now());
INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('54609386-7ee7-45da-a762-fe690adf7d56', '4614a359-6770-44d8-a6f8-2533d961b7aa', 'CLOSED_SINGLE', 'Tikinti şirkətində 13 il çalışan A şirkətin ləğv edilməsi səbəbindən işdən çıxarılacağını öyrənir. Şirkətin rəhbərliyi A ilə birlikdə digər işçilərə də xəbərdarlıq məktubu göndərərək işdən azad edilmələri barədə məlumat verir. A-ya bildirilir ki, onun əmək müqaviləsi 5 ay sonra ləğv olunacaq. A-nın eyni şirkətdə uzun müddət təcrübəsi və beynəlxalq dərəcəli xüsusi layihələrdə iştirakı nəzərə alınsa da, ona əlavə kompensasiya təklif edilmir. A bu qərarla razılaşmır və rəhbərliyə müraciət edərək, şirkətin ləğvi zamanı ona orta aylıq əməkhaqqının azı iki misli miqdarında işdənçıxarma müavinətinin ödənilməsini tələb edir. Rəhbərlik isə cavabında qanunla nəzərdə tutulmuş müddətə uyğun xəbərdarlıq verildiyini və əlavə kompensasiyanın zəruri olmadığını bildirir. A bu vəziyyətdə hüquqlarının pozulduğunu düşünür və müvafiq dövlət orqanına müraciət etmək qərarına gəlir. Azərbaycan Respublikasının Əmək Məcəlləsinə əsasən situasiya ilə bağlı doğru mülahizəni seçin.', 1, 17, NULL, now(), now());
