INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('25c90dc5-fc22-40d9-8af9-4e9bf27119aa', '77f40683-4fbb-4c27-abce-05b8830a195c', 'CLOSED_SINGLE', 'A və B arasında 2024-cü ilin mart ayında notarial qaydada təsdiq edilmiş borc müqaviləsi bağlanmışdır. Müqaviləyə əsasən, A, B-yə 30.000 manat pul vəsaiti vermiş və müqavilədə həmin məbləğin 6 ay müddətində geri qaytarılması öhdəliyi B-nin üzərinə qoyulmuşdur. Müddət bitdikdə B borcu geri qaytarmamış, A isə əvvəlcə yazılı tələblərlə müraciət etmiş, lakin nəticə əldə olunmadıqda Bakı Kommersiya Məhkəməsinə iddia ərizəsi ilə müraciət etmişdir. Məhkəmə işə baxaraq tərəflər arasında rəsmi müqavilənin mövcudluğunu, borcun verilməsi və ödənilməməsini təsdiqləyən sübutları nəzərə alaraq A-nın xeyrinə qərar çıxarmışdır. Qərar əsasən, B borcu tam şəkildə A-ya qaytarmalı idi. Lakin B bu qərarla razılaşmadığını bildirərək, məhkəmənin qərarından şikayət vermək niyyətində olduğunu bildirib. “Məhkəmələr və hakimlər haqqında” Azərbaycan Respublikasının Qanununa əsasən situasiya ilə bağlı doğru fikiri seçin.', 1, 1, NULL, now(), now());
