INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('9309a1ee-0988-489d-830a-715f5eaa02fc', '8604b35c-a756-4a54-85a6-723c728ad1f8', 'CLOSED_SINGLE', '“Dövlət orqanlarında, dövlət mülkiyyətində olan və paylarının (səhmlərinin) nəzarət zərfi dövlətə məxsus olan hüquqi şəxslərdə və büdcə təşkilatlarında kargüzarlığın aparılmasına dair Təlimat”a əsasən yanlışdır: 1. Sənəd dövriyyəsi kiçik olan təşkilatlarda ayrı-ayrı sənəd növləri üçün blanklar tətbiq edilir və onların üzərində mətbəə üsulu ilə sənəd növünün adı (qərar, protokol, əmr və s.) yazılır. 2. Təşkilatlarda tez-tez təkrarlanan sərəncam mahiyyətli sənədlər tərtib edilərkən sənədlərin mətbəə üsulu ilə hazırlanan trafaret mətnli blankları tətbiq edilməlidir. 3. Yuxarı təşkilatların konkret tapşırıqları təşkilatlarda sərəncam mahiyyətli sənədlərin yaranmasına hüquqi əsas ola bilər. 4. Bir qayda olaraq, sənədlərin mətnləri iki hissədən ibarət olur. Birinci hissədə sənədin hazırlanması üçün əsas götürülmüş faktların və hadisələrin təsviri verilir. İkinci hissədə nəticələr, təkliflər, qərarlar və sərəncamlar ifadə olunur. 5. Yerli icra hakimiyyəti orqanlarında kargüzarlıq bu təlimat əsasında aparılır.', 1, 24, NULL, now(), now());
INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('9ed76f7d-1b8b-4381-b810-9acc94f7ae5a', '8604b35c-a756-4a54-85a6-723c728ad1f8', 'CLOSED_SINGLE', 'Qida Təhlükəsizliyi Agentliyinə icra olunmaq üçün sənəd daxil olmuşdu. Sənəd qeydiyyata alındıqdan sonra icra üçün aidiyyəti üzrə Heyvan sağlamlığı və bioloji təhlükəsizlik şöbəsinə göndərilmişdi. Həmin şöbədə sənəd üzrə icraya başlanılmış, lakin hələ sənədin icrası tamamlanmamışdı. Bu zaman kargüzarlıq xidməti sənədin yerini və vəziyyətini yoxlamaq məqsədilə Heyvan sağlamlığı və bioloji təhlükəsizlik şöbəsindən sənədi təqdim etməyi tələb etmişdi. Heyvan sağlamlığı və bioloji təhlükəsizlik şöbəsi isə sənədin icrası tam başa çatmadığı üçün onu kargüzarlıq xidmətinə təqdim etməmişdi. Bununla belə, sənədin icra mərhələsində olduğu barədə məlumat kargüzarlıq xidmətinə bildirilmişdi. “Dövlət orqanlarında, dövlət mülkiyyətində olan və paylarının (səhmlərinin) nəzarət zərfi dövlətə məxsus olan hüquqi şəxslərdə və büdcə təşkilatlarında kargüzarlığın aparılmasına dair Təlimat”a əsasən situasiya ilə bağlı doğrudur:', 1, 25, NULL, now(), now());
