INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('683937c7-53db-4599-aa15-da3991bcdfda', '953731c5-1f0a-420e-b886-ce04bfbbba5a', 'CLOSED_SINGLE', 'Azərbaycan Respublikasının Mülki Məcəlləsinə əsasən Hüquqi şəxs adından çıxış edən şəxs, o cümlədən hüquqi şəxsin idarəetmə orqanlarında (müşahidə (direktorlar) şurası, icra orqanı) təmsil olunan hər hansı şəxs hüquqi şəxsə və ya səhmdara (payçıya) dəyən zərərə görə hansı hallarda məsuliyyət daşıyır? 1. Hüquqi şəxsin əmlakının bazar şərtlərindən əhəmiyyətli dərəcədə aşağı şərtlərlə verilməsi 2. Hüquqi şəxsin bağlanmış müqavilələr əsasında malları onların real dəyərindən əhəmiyyətli dərəcədə aşağı qiymətə satın alması 3. Hüquqi şəxsin zərərlə işlədiyi halda və ya hüquqi şəxsin mənfəətinə qeyri-mütənasib həcmdə hüquqi şəxsin idarəetmə orqanlarının üzvlərinə bonusların ödənilməməsi 4. Özü üçün, hüquqi şəxsə aidiyyəti olan şəxslər və ya digər şəxslər üçün maddi və qeyri-maddi əmlak nemətləri və bu cür əmlaka hüquqların təmin edilməsi məqsədi ilə hüquqi şəxsin əmlakının mənimsənilməsi və ya israf edilməsi', 1, 38, NULL, now(), now());
