INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('320b7b4b-2455-4dab-97d3-bc5a67c6c80f', '9a653e20-e571-4419-b500-00296b3edcc5', 'CLOSED_SINGLE', '“Elektron imza və elektron sənəd haqqında” Azərbaycan Respublikasının qanununa əsasən gücləndirilmiş elektron imzayla bağlı doğrudur : 1. İmza sahibinin nəzarəti altında olan elektron imza vasitələri ilə yaradılan və həmçinin imza sahibinə məxsus olmaqla onu identikləşdirən elektron imza ; 2.Əlaqəli olduğu məlumat bildirişinin bütövlüyünü, dəyişməzliyini, pozulmazlığını və saxtalaşdırılmadığını müəyyən etməyə imkan verən elektron imza ; 3.Əlaqəli olduğu məlumat bildirişinin bütövlüyünü, dəyişməzliyini, təhrif olunmadığını və saxtalaşdırılmadığını müəyyən etməyə imkan verən elektron imza ; 4. İmza sahibinin nəzarəti altında olan elektron imza vasitələri ilə yaradılan və yalnız imza sahibinə məxsus olmaqla onu identikləşdirən elektron imza.', 1, 4, NULL, now(), now());
