INSERT INTO questions (id, test_id, question_type, question_text, score, order_index, correct_answer, created_at, updated_at) VALUES ('c97f89c4-28b1-4d35-bf7d-5f7db10c14d9', 'eb5c9df4-ae8f-43dd-ad53-fc0def743537', 'CLOSED_SINGLE', 'Akşin müddətli həqiqi hərbi xidmətə çağırılmazdan əvvəl dövlət qulluğuna qəbulun test mərhələsində iştirak edərək sertifikatı əldə etdikdən sonra müsahibədə iştirak etmədən birbaşa hərbi xidmətə yollanıb. Hərbi xidməti başa vurduqdan sonra Bakı Şəhər İcra Hakimiyyətində aparıcı məsləhətçi vəzifəsi üzrə elan olunan müsahibədən uğurlu nəticə əldə edərək 15.09.21 tarixində ilk dəfə daimi dövlət qulluğuna qəbul olunub. Xidmətin 5-ci ilinə keçəndə isə maaşı aldıqda aylıq əlavə haqqın 5% hesablandığını görür, bununla bağlı müraciət etdikdə isə mühasib, Akşinin 2025-ci ildə fəaliyyətə başladığını, bu səbəbdən 4 illik qulluq stajına malik olduğunu bildirir. Akşin isə 2020-2021-ci illərdə müddətli həqiqi hərbi xidmətdə olduğunu və həmin müddətində nəzərə alınmalı olduğunu bildirir. Dövlət orqanlarında dövlət qulluqçusuna dövlət qulluğunda qulluq stajına görə aylıq əlavə haqqın məbləğinin müəyyənləşdirilməsi və ödənilməsi qaydasına əsasən situasiya ilə bağlı doğrudur:', 1, 64, NULL, now(), now());
